import { useState } from 'react';

function Exam03(){
    const[number, setNumber] = useState(0);
    const [withdrawAmount, setWithdrawAmount] = useState(null);

    function one() {
        setNumber(10000);
        setWithdrawAmount(10000);
    }
    function five() {
        setNumber(50000);
        setWithdrawAmount(50000);
    }
    function ten() {
        setNumber(100000);
        setWithdrawAmount(100000);
    }
    function reset() {
        setNumber(0);
        setWithdrawAmount(null);
    }

    return (
        <>
            <h1>세 번째 예제</h1>

            <button onClick={one}> 1만원 </button>
            <button onClick={five}> 5만원 </button>
            <button onClick={ten}> 10만원 </button>
            <button onClick={reset}> 초기화</button>
            <br/>

            {withdrawAmount !== null && (
                <div>
                    <p>출금 금액: {withdrawAmount}원</p>
                </div>
            )}

        </>
    );
}

export default Exam03;
