import React, { useState } from "react";

const Exam07 = () => {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [pwok, setPwok] = useState("");

  const handleIdChange = (e) => {
    setId(e.target.value);
  };

  const handlePwChange = (e) => {
    setPw(e.target.value);
  };

  const handlePwokChange = (e) => {
    setPwok(e.target.value);
  };

  const handleMemberOk = (e) => {
    e.preventDefault();

    if (id && pw && pw === pwok) {
      alert("회원가입이 완료되었습니다.");
    } else {
      alert("입력 정보를 다시 확인해주세요.");
    }
  };

return (
    <div>
      <h1>회원가입</h1>
      <form onSubmit={handleMemberOk}>
        <div>
          <label>아이디:</label>
          <input type="text" value={id} onChange={handleIdChange} />
        </div>
        <div>
          <label>비밀번호:</label>
          <input type="password" value={pw} onChange={handlePwChange} />
        </div>
        <div>
          <label>비밀번호 확인:</label>
          <input type="password" value={pwok} onChange={handlePwokChange} />
        </div>
        <button type="submit">가입하기</button>
      </form>
    </div>
  );
};

export default Exam07;
