import React, { useState } from 'react';

function App() {
  const [text, setText] = useState('');

  const handleChange = (event) => {
    const inputText = event.target.value;
    if (inputText.length <= 1000) {
      setText(inputText);
    }
  };

  return (
    <div>
      <textarea
        value={text}
        onChange={handleChange}
        rows={10} // 원하는 만큼의 행을 지정하세요.
        cols={30} // 원하는 만큼의 열을 지정하세요.
        maxLength={1000} // 최대 길이를 1000으로 설정합니다.
      />
      <div>
        {text.length}/1000
      </div>
    </div>
  );
}

export default App;
