    const[size, setSize] = useState(200);

    function small() {
        // size = 100; // React 사용불가 
        setSize(100); 
    }



    return (
        <>
        <h1>두 번째 예제</h1>

        <button> 작게 </button>
        <button> 기본 </button>
        <button> 크게 </button>
        <br/>

        <img src={lion} width={200}/>

        </>

    );
}



export default Exam02;
